createUserTable = "CREATE TABLE X_USERS( ID INT PRIMARY KEY AUTO_INCREMENT, USERNAME VARCHAR(255) UNIQUE, PASSWORD VARCHAR(255), FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), PHONENUMBER VARCHAR(255), MOBILEPHONENUMBER VARCHAR(255), ACTIVATED TINYINT(1) DEFAULT 0, CREATEDDATE DATETIME DEFAULT CURRENT_TIMESTAMP, UPDATEDDATE DATETIME DEFAULT CURRENT_TIMESTAMP );"
insertUserToUserTable = "INSERT INTO X_USERS (USERNAME, PASSWORD, FIRSTNAME, LASTNAME, PHONENUMBER, MOBILEPHONENUMBER) VALUES ('%s','%s','%s','%s','%s','%s')"